CREATE TABLE `USER` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `fullname` varchar(50),
  `dayofbirth` date,
  `email` varchar(150),
  `PhoneNumber` varchar(20),
  `address` varchar(200),
  `avartar` varchar(255),
  `account` varchar(50),
  `password` varchar(32),
  `role_id` integer,
  `created_at` datetime,
  `updated_at` datetime
);

CREATE TABLE `User_role` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `Role_type` varchar(255)
);

CREATE TABLE `DVD` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `category_id` integer,
  `title` varchar(250),
  `price` int,
  `Quantity` int,
  `description` longtext,
  `productimage` varchar(255),
  `discount_id` int,
  `created_at` datetime,
  `updated_at` datetime
);

CREATE TABLE `DVDCategory` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `name` varchar(100)
);

CREATE TABLE `INVOICE` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `user_id` integer,
  `fullname` varchar(50),
  `email` varchar(150),
  `phone_number` varchar(20),
  `address` varchar(200),
  `note` varchar(1000),
  `order_date` datetime,
  `status` varchar(200),
  `total_money` int
);

CREATE TABLE `Invoice_Detail` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `order_id` int,
  `product_id` int,
  `price` int,
  `num` int,
  `total_money` int
);

CREATE TABLE `OFFER` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `name` varchar(100),
  `offerimage` varchar(255),
  `start_date` datetime,
  `end_date` datetime,
  `status` varchar(50),
  `code` varchar(50),
  `discount_percentage` int 
);

CREATE TABLE `User_cart` (
  `id` integer PRIMARY KEY AUTO_INCREMENT,
  `user_id` integer,
  `dvd_id` integer,
  `dvd_title` varchar(250),
  `dvd_price` int,
  `quantity` int,
  `productimage` varchar(250),
  `order_total` integer,
  `created_at` datetime,
  `updated_at` datetime
);


ALTER TABLE `INVOICE` ADD FOREIGN KEY (`user_id`) REFERENCES `USER` (`id`);

ALTER TABLE `Invoice_Detail` ADD FOREIGN KEY (`product_id`) REFERENCES `DVD` (`id`);

ALTER TABLE `USER` ADD FOREIGN KEY (`role_id`) REFERENCES `User_role` (`id`);

ALTER TABLE `DVD` ADD FOREIGN KEY (`category_id`) REFERENCES `DVDCategory` (`id`);

ALTER TABLE `DVD` ADD FOREIGN KEY (`discount_id`) REFERENCES `OFFER` (`id`);

ALTER TABLE `Invoice_Detail` ADD FOREIGN KEY (`order_id`) REFERENCES `INVOICE` (`id`);

ALTER TABLE `User_cart` ADD FOREIGN KEY (`user_id`) REFERENCES `USER` (`id`);

ALTER TABLE `User_cart` ADD FOREIGN KEY (`dvd_id`) REFERENCES `DVD` (`id`);


